import './App.css'
import { useState , useEffect } from 'react';
import { io } from "socket.io-client";
import { socket } from 'socket.io-client';

/* UI Components */

// Container for Chatbox and ChatList Containers.
function MainBox ({ socket }) {

  const username = "User1";
  const [users, setUsers] = useState([]);
  const [messages, setMessages] = useState([]); // Create a STATE of chatbox that retains messages already rendered across multiple re-renders.
  const [receiver, setReceiver] = useState();

  // Listen for messages continuously.
  useEffect(() => {
    socket.on('receive-message', (data) => {
      setMessages((messages) => [...messages,{ sender: data.sender, msg: data.message }]);
    });
    // Clean up the socket listener when component unmounts.
    return () => { socket.off('receive-message'); }
  }, [socket]);

  return (
    <>
    <div className="mainbox">
        <ChatList messages={messages} setMessages={setMessages} users={users} setUsers={setUsers} receiver={receiver} setReceiver={setReceiver} socket={socket} />
        <ChatBox  username = {username} receiver={receiver} messages={messages} setMessages={setMessages} socket={socket} />
    </div>
    </>
  )
}

// Chat Container Component
function ChatBox ({ username , receiver , messages , setMessages , socket }) {

  const [inputText, setInputText] = useState(''); // Create a STATE of Input Bar that holds input text until its sent.

  const getInputText = (e) => {
    setInputText(e.target.value);
  }

  const renderSentMessage = () => {
    if (inputText.trim()) { // Check if input is empty or NOT.
      setMessages([...messages,{ sender : 'User1', msg : inputText}]); // Add Message to chatbox for rendering.
      sendMessage(socket,username,receiver,inputText);
      setInputText(''); // Clear Input Box
    }
  }

  const autoResize = (e) => {
    const textarea = e.target;
    // Reset the height to 'auto' to shrink before expanding
    textarea.style.height = 'auto';
    // Adjust height to scrollHeight dynamically
    textarea.style.height = `${textarea.scrollHeight}px`;

    // Set maximum height limit (example: 12vh)
    if (textarea.scrollHeight > 12 * window.innerHeight / 100) {
      textarea.style.height = `${12 * window.innerHeight / 100}px`; // Apply max height
    }
  };

  return (
    <>
    <div className="chatbox">
      <div className='message-container'>
        { 
          messages.map(
            (message,index) => (
            <Message key={index} sender={message.sender} msg={message.msg} />
            )
          )
        }
      </div>
      <div className="input-container">
        <Button name='SEND' onClick={renderSentMessage} />
        <textarea 
          placeholder='Send Message' 
          className='inputbar' 
          value={inputText} 
          onChange={getInputText} 
          onInput={autoResize}
        >
        </textarea>
      </div>
    </div>
    </>
  )
}

// Chat List Container Component
function ChatList ({ MESSAGES , setMessages,  users , setUsers , setReceiver , socket }) {

    async function FetchPreviousMessages (receiver) {
      try {
        setReceiver(receiver);
        const response = await fetch(`/api/messages/${receiver}`);
        if (response.ok) {
            const messages = await response.json();
            setMessages(messages.map((msg) => ({ sender: msg.sender_id, msg: msg.msg })));
        } 
        else { console.error("Failed to load messages:", response.statusText); }
      } 
      catch (error) { console.error("Error fetching messages:", error); }
    }

  return (
    <>
    <div className="chatlist">
      { 
        users.map( (user,index) => (
          <UserElement key={index} username={user} onClick={FetchPreviousMessages(user)}/>
        ))
      }
    </div>
    </>
  )
}

function UserElement ({ username , onClick }) {
  return (
    <>
    <div className="user-wrapper">
      <button className="userelement" onClick={onClick}>
        { username }
      </button>
    </div>
    </>
  )
}

// Navigation Bar Component
function Navbar () {
  return (
    <>
    <div className="navbar">
      <Button name='PROFILE' />
      <Button name='LOG OUT' />
      <Button name='ABOUT US' />
    </div>
    </>
  )
}

// Button Component
function Button ({ name , onClick }) {
  return (
    <>
    <button className="Button" onClick={onClick}>
        {name}
    </button>
    </>
  )
}

// Message Component
function Message ({ sender , msg }) {
  return (
    <>
    <div className="message">
        {sender} : {msg}
    </div>
    </>
  )
}

/* Logic Part */
function sendMessage ( socket , sender , receiver , msg ) {

  if (!receiver || !msg) { return; }

  // Emit the message to the server
  socket.emit('send-message', {
      message: msg,
      sender: sender, 
      receiver: receiver
  });

}

// Main Page
export default function Page () {
  const socket = io(); // Initialize socket here

  useEffect(() => {
    // Register the user when the component mounts
    const username = "HI";
    socket.emit('register', username);
  }, [socket]); // Ensure socket is initialized once on component mount

  return (
    <div className='Page'>
      <Navbar />
      <MainBox socket={socket} />
    </div>
  );
}
