// Writing Space Component
function Writingspace() {
    const editorRef = useRef(null); // Create a ref for the editor
    const quillRef = useRef(null); // Create a ref to store Quill instance

    useEffect(() => {
        // Initialize Quill only when the component mounts
        if (quillRef.current === null) {
            quillRef.current = new Quill(editorRef.current, {
                theme: 'snow', // Specify theme
                modules: {
                    toolbar: [
                        [{ 'header': [1, 2, false] }],
                        ['bold', 'italic', 'underline'],
                        [{ 'list': 'ordered' }, { 'list': 'bullet' }],
                        [{ 'color': [] }, { 'background': [] }],
                        ['link', 'image', 'code-block'],
                        ['clean'], // Remove formatting button
                        ['undo', 'redo'] // Custom undo and redo buttons
                    ]
                }
            });
        }

        // Cleanup function to remove the editor if needed
        return () => {
            if (quillRef.current) {
                quillRef.current = null; // Clear the reference
            }
        };
    }, []); // Empty dependency array means this effect runs once on mount

    return (
        <div className='Writingspace'>
            <div ref={editorRef} style={{ height: '75vh' }} /> {/* Height of the editor */}
        </div>
    );
}


export default function Editor () {
    return (
        <>
        <div className='main'>
            <Writingspace />
        </div>
        </>
    );
}
