import React, { useEffect, useRef } from 'react';
import './Editor.css'
import 'quill/dist/quill.snow.css'; // Import Quill's CSS
import Quill from 'quill'; // Import Quill

// Toolbar Component
function ToolbarButton ({ title , icon }) {
    return (
        <>
        <button type='button' className='Toolbar_button'>
            {/* {icon && <img src={icon} alt={`${title} icon`} style={{ marginRight: '0.5vw', height: '20px', width: '20px' }} />} */}
            {title}
        </button>
        </>
    )
}

function Toolbar () {
    return (
        <>
        <div className='Toolbar'>
            <ToolbarButton title='Save' icon=''/>
            <ToolbarButton title='Select' icon=''/>
            <ToolbarButton title='Draw' icon=''/>
            <ToolbarButton title='Text' icon=''/>
            <ToolbarButton title='Highlight' icon=''/>
            <ToolbarButton title='Zoom In' icon=''/>
            <ToolbarButton title='Zoom Out' icon=''/>
            <ToolbarButton title='Undo' icon=''/>
            <ToolbarButton title='Redo' icon=''/>
            <ToolbarButton title='Fit to Screen' icon=''/>
            <ToolbarButton title='Redo' icon=''/>
            <ToolbarButton title='Redo' icon=''/>
            <ToolbarButton title='Redo' icon=''/>
            <ToolbarButton title='Redo' icon=''/>
            <ToolbarButton title='Redo' icon=''/>
            <ToolbarButton title='Redo' icon=''/>
            <ToolbarButton title='Redo' icon=''/>
            <ToolbarButton title='Redo' icon=''/>
            <ToolbarButton title='Redo' icon=''/>
            <ToolbarButton title='Redo' icon=''/>
            <ToolbarButton title='Redo' icon=''/>
        </div>
        </>
    );
}


// Writing Space Component
// Writing Space Component
function Writingspace() {
    const editorRef = useRef(null); // Create a ref for the editor

    useEffect(() => {
        // Initialize Quill only when the component mounts
        let quill = new Quill(editorRef.current, {
            theme: 'snow', // Specify theme
            modules: {
                toolbar: [
                    [{ 'header': [1, 2, false] }],
                    ['bold', 'italic', 'underline'],
                    ['image', 'code-block'],
                    ['clean'] // remove formatting button
                ]
            }
        });

        // Cleanup function to remove the editor if needed
        return () => {
            quill.disable(); // Optional: Disable the editor
            quill = null; // Optional: Clear the reference
        };
    }, []); // Empty dependency array means this effect runs once on mount

    return (
        <div className='Writingspace'>
            <div ref={editorRef} style={{ height: '400px' }} /> {/* Height of the editor */}
        </div>
    );
}

export default function Editor () {
    return (
        <>
        <div className='main'>
            <Toolbar />
            <Writingspace />
        </div>
        </>
    );
}
